// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract GasEstimator {
    string greeting = "Default-Greeting-From-Solidity";
    uint16 count = 0;

    function setGreeting(string memory _newGreeting) public {
        emit NewGreetingEvent(msg.sender, greeting, _newGreeting);
        greeting = _newGreeting;
        ++count; 
    }

    // update text
    function getGreeting() public view returns (string memory) {
        return greeting;
    }

    function hugeLoopPure() public pure {
        uint32 i = 0;
        uint32 j = 0;
        for (i = 0; i < 32000; i++) {
            ++j;
        }
    }

    function infiniteLoopPure() public pure {
        uint32 i = 0;
        while (true) {
            i += 1;
        }
    }

    /*function infiniteLoopView() public view returns (uint16 count) {
        uint32 ii = 0;
        while (true) {
            ii += 1;
        }
        return count;
    }*/

    event NewGreetingEvent(address _sender, string _oldGreeting, string _newGreeting);
}
