// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;
contract Identity {
    bytes public memoryStored;

    function callDatacopy(bytes memory data) public returns (bytes memory) {
        bytes memory result = new bytes(data.length);
        assembly {
            let len := mload(data)

            // call : uses new storage context, msg.send will be the Identity contract msg.value will not be same as Identity value
            // delegatecall; it uses current contracts storage, current msg.sender and current msg.value
            // callcode is deprecated; use delegatecall instead
            // official doc: https://docs.soliditylang.org/en/v0.8.14/introduction-to-smart-contracts.html?highlight=callcode#delegatecall-callcode-and-libraries
            // memory layout: https://docs.soliditylang.org/en/v0.8.7/internals/layout_in_memory.html

            //if iszero(call(gas(), 0x04, 0, add(data, 0x20), len, add(result,0x20), len)) {
            //if iszero(delegatecall(gas(), 0x04, add(data, 0x20), len, add(result,0x20), len)) {
            //if iszero(callcode(gas(), 0x04, 0, add(data, 0x20), len, add(result,0x20), len)) {
            //if iszero(call(gas(), 0x04, 0, add(data, 0x20), len, add(result,0x20), len)) {
            if iszero(
                delegatecall(
                    gas(),
                    0x04,
                    add(data, 0x20),
                    len,
                    add(result, 0x20),
                    len
                )
            ) {
                invalid()
            }
        }

        memoryStored = result;

        return result;
    }

    function getMemoryStored() public view returns (bytes memory) {
        return memoryStored;
    }
}

